@Composable
fun UserPictureAtm(
    modifier: Modifier = Modifier,
    data: UserPictureAtmData,
    onUIAction: (UIAction) -> Unit
) {
    val photo = remember {
        mutableStateOf<ImageBitmap?>(null)
    }

    LaunchedEffect(key1 = Unit) {
        coroutineScope {
            if (data.docPhoto != null) {
                launch(Dispatchers.IO) {
                    val byteArray = Base64.decode(data.docPhoto, Base64.DEFAULT)
                    BitmapFactory.decodeByteArray(byteArray, 0, byteArray.size)?.let {
                        photo.value = it.asImageBitmap()
                    }
                }
            }
        }
    }
    photo.value?.let {
        Image(
            modifier = modifier
                .size(140.dp)
                .background(color = White)
                .clip(shape = RoundedCornerShape(50)),
            bitmap = it,
            contentScale = ContentScale.FillWidth,
            contentDescription = ""
        )
    } ?: Image(
        modifier = modifier
            .size(140.dp)
            .background(color = White)
            .clip(shape = RoundedCornerShape(50))
            .noRippleClickable {
                onUIAction(
                    UIAction(
                        actionKey = data.actionKey,
                        action = data.action
                    )
                )
            }
            .semantics {
                testTag = data.componentId.orEmpty()
            },
        contentScale = ContentScale.Fit,
        painter = painterResource(
            id = DiiaResourceIcon.getResourceId(data.defaultImageCode?.code.orEmpty())
        ),
        contentDescription = ""
    )
}

data class UserPictureAtmData(
    val actionKey: String = UIActionKeysCompose.USER_PICTURE_ATM,
    val componentId: String?,
    var docPhoto: String?,
    val defaultImageCode: UiIcon.DrawableResource?,
    val action: DataActionWrapper?
) : UIElementData

